module.exports = function(RED){

    function Mynode(config){
        RED.nodes.createNode(this,config);
        var node = this;
        console.log("INICIANDO MI NODO")
        //cerrar  mi nodo cuando se cierre el programa nodeRED
        node.on('close', function(){
            console.log('cerrando nodo')
        });
        ///creando el evento input si recibo un mensaje
        //msg puede ser un objeto o un array de pende mi la cantidad de entradas y salidas.
        node.on('input', function(msg){
            msg.payload = msg.payload={"msg":"ESte es mi mensaje"};
            console.log("llamada a mi nodo");
            node.send(msg); //sacar la informacion de mi nodo 
        });

    }
    RED.nodes.registerType("mynode",Mynode);

}